import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import './sidebar.css';
import axios from "axios";
const Sidebar = () => {
  const data = [

  ];
  const [Data, setData] = useState([]);
  const headers = {
    'id': localStorage.getItem("id"),
    'authorization': `Bearer ${localStorage.getItem("token")}`,
  };

  useEffect(() => {
    const fetch = async () => {
      try {
        const response = await axios.get("http://localhost:4000/api/v2/get-all-tasks", { headers });
        setData(response.data.data);
      } catch (error) {
        console.error("Error fetching data:", error);
        // Optionally set error state and display a message
      }
    };
    fetch();
  }, []);

  return (
    <aside className="sidebar">
      <nav>
      {Data && Data.length > 0 ? (
      <div>
        <h2>{Data.username}</h2> {/* Example: Displaying the first user */}
        <h4>{Data.email}</h4>
        <hr/>
      </div>
    ) : (
      <p>No user data available</p>
    )}
        <ul>
          <li><Link to="/"><i className="bi bi-house"></i> All tasks</Link></li>
          <li><Link to="/important"><i className="bi bi-star"></i> Important tasks</Link></li>
          <li><Link to="/completed"><i className="bi bi-check-circle"></i> Completed tasks</Link></li>
          <li><Link to="/incompleted"><i className="bi bi-x-circle"></i> Incompleted tasks</Link></li>
          <li><Link to="/trash"><i className="bi bi-trash"></i> Trash</Link></li>
        </ul>
      </nav>
    </aside>
  );
};

export default Sidebar;
