import React, { useEffect } from 'react';
import { Link } from 'react-router-dom';
import './sidebar.css';
import axios from "axios";
const Sidebar = () => {
  const headers = {
    id: localStorage.getItem("id"),
    authorization: localStorage.getItem("token"),
  };
  useEffect(() => {
    const fetch = async () => {
      await axios.get("http://localhost:4000/api/v2/get-all-tasks");
    }
  }, []);
  return (
    <aside className="sidebar">
      <nav>
        <ul>
          <li><Link to="/"><i className="bi bi-house"></i> All tasks</Link></li>
          <li><Link to="/important"><i className="bi bi-star"></i> Important tasks</Link></li>
          <li><Link to="/completed"><i className="bi bi-check-circle"></i> Completed tasks</Link></li>
          <li><Link to="/incompleted"><i className="bi bi-x-circle"></i> Incompleted tasks</Link></li>
          <li><Link to="/trash"><i className="bi bi-trash"></i> Trash</Link></li>
        </ul>
      </nav>
    </aside>
  );
};

export default Sidebar;
