import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import './sidebar.css';
import axios from "axios";
const Sidebar = () => {
  const data = [

  ];
  const [Data, setData] = useState();
  const headers = {
    'id': localStorage.getItem("id"),
    'authorization': `Bearer ${localStorage.getItem("token")}`,
  };

  useEffect(() => {
    const fetch = async () => {
      try {
        const response = await axios.get("http://localhost:4000/api/v2/get-all-tasks", { headers });
        setData(response.data.data);
      } catch (error) {
        console.error("Error fetching data:", error);
        // Optionally set error state and display a message
      }
    };
    fetch();
  }, []);

  return (
      <nav className="sidebar">
        <ul>
          <li><Link to="/"><i className="bi bi-house"></i> <h6>All tasks</h6></Link></li>
          <li><Link to="/important"><i className="bi bi-star"></i> <h6>Important tasks</h6></Link></li>
          <li><Link to="/completed"><i className="bi bi-check-circle"></i> <h6>Completed tasks</h6></Link></li>
          <li><Link to="/incompleted"><i className="bi bi-x-circle" id='icon'></i> <h6>Incompleted tasks</h6></Link></li>
        </ul>
      </nav>
  );
};

export default Sidebar;
