import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom'; // Updated import
import './updateProfile.css'; // Ensure the path is correct

const UpdateProfile = ({ onSave, onClose }) => {
  const [username, setUsername] = useState('');
  const [email, setEmail] = useState('');
  const [oldPassword, setOldPassword] = useState('');
  const [password, setPassword] = useState('');
  const navigate = useNavigate(); // Use useNavigate

  const handleSaveChanges = (e) => {
    e.preventDefault();
    // Add validation if necessary
    onSave({ username, email, oldPassword, password });
  };

  const handleClose = () => {
    if (onClose) onClose();
    navigate('/home'); // Redirect to the home page
  };

  return (
    <div className="update-profile-overlay">
      <div className="update-profile-popup">
        <button type="button" className="close-btn" onClick={handleClose}>
          &times;
        </button>
        <h5 id='update'>Update Profile</h5>
        <hr/>
        <form onSubmit={handleSaveChanges}>
          <div className="form-group">
            <label>Username:</label>
            <input
              type="text"
              className="form-control"
              placeholder="Username..."
              value={username}
              onChange={(e) => setUsername(e.target.value)}
            />
          </div>
          <div className="form-group">
            <label>Email address:</label>
            <input
              type="email"
              className="form-control"
              placeholder="email@example.com"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
            />
          </div>
          <div className="form-group">
            <label>Old password:</label>
            <input
              type="password"
              className="form-control"
              placeholder="old-password..."
              value={oldPassword}
              onChange={(e) => setOldPassword(e.target.value)}
            />
          </div>
          <div className="form-group">
            <label>New Password:</label>
            <input
              type="password"
              className="form-control"
              placeholder="password..."
              value={password}
              onChange={(e) => setPassword(e.target.value)}
            />
          </div>
          <div className="form-actions">
            <button type="submit" className="btn btn-primary">
              Save Changes
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default UpdateProfile;
