        const response = await axios.post(
          "http://localhost:4000/api/v1/log-in", 
          Data, 
          {
            headers: {
              'Content-Type': 'application/json',
            },
            withCredentials: true, // Include credentials if your backend needs it (cookies, etc.)
          }
        );
        setData({ username: "", password: "" });
        localStorage.setItem("id", response.data.id);
        localStorage.setItem("token", response.data.token);
        dispatch(authActions.login());
        navigate("/");
      }
    } catch (error) {
      alert("An error occurred during login");
    }
  };*/


  return (
    <div className="container">
      <div className="header">
        <div className="text">{action}</div>
        <div className="underline"></div>
      </div>
      <div className="inputs">
        <div className="input">
          <img src="./img/icons8-person-24.png" alt="person icon" />
          <input
            type="text"
            name="username"
            placeholder="Username"
            required
           // value={Data.username}
            //onChange={change}
          />
        </div>

        <div className="input">
          <img id="password" src="./img/icons8-password-50.png" alt="password icon" />
          <input
            type="password"
            name="password"
            placeholder="Password"
            required
            /*value={Data.password}
            onChange={change}*/
          />
        </div>
      </div>

      <div className="submit-container">
       //<button className="submit" onClick={submit}>Login</button>
        <button className="submit" onClick={submit}>Login</button>

      </div>
      <div className="forgot-password">
        Lost Password? <span>Click here</span>
      </div>

      <div className="forgot-password">
        Not having an account? <Link to="/signUp" className="signup-link">Sign Up here</Link>
      </div>
    </div>
  );
}

export default SignIn;
