import React, { useState } from 'react';
import './Task.css';

const Task = ({ task, onDelete, onToggleImportant, onToggleCompletion }) => {
  const [isEditing, setIsEditing] = useState(false);
  const [editedTitle, setEditedTitle] = useState(task.title);
  const [editedDescription, setEditedDescription] = useState(task.description);

  const handleSaveEdit = async () => {
    try {
      const userId = localStorage.getItem('id');
      await axios.put(`/api/update-task/${task._id}`, {
        title: editedTitle,
        description: editedDescription
      }, {
        headers: { id: userId }
      });
      setIsEditing(false);
    } catch (error) {
      console.error('Error saving task edit:', error);
    }
  };

  return (
    <div className="task">
      <div className="task-content">
        {isEditing ? (
          <>
            <input
              type="text"
              value={editedTitle}
              onChange={(e) => setEditedTitle(e.target.value)}
            />
            <textarea
              value={editedDescription}
              onChange={(e) => setEditedDescription(e.target.value)}
            ></textarea>
            <button onClick={handleSaveEdit}>Save</button>
          </>
        ) : (
          <>
            <h3>{task.title}</h3>
            <p>{task.description}</p>
          </>
        )}
      </div>
      <div className="task-buttons">
        <button onClick={() => onToggleCompletion(task._id)}>
          {task.complete ? <h6>Completed</h6> : <h6>Incomplete</h6>}
        </button>
        <button onClick={() => onToggleImportant(task._id)}>
          {task.important ? <i className="bi bi-star-fill" style={{ color: 'yellow' }}></i> : <i className="bi bi-star"></i>}
        </button>
        <button onClick={() => setIsEditing(prev => !prev)}>
          {isEditing ? 'Cancel' : 'Edit'}
        </button>
        <button onClick={() => onDelete(task._id)}>
          <i className="bi bi-trash"></i>
        </button>
      </div>
    </div>
  );
};

export default Task;
