import React, { useState, useEffect } from 'react';
import Task from './Task';
import InputData from './inputData';
import './tasklist.css';
import axios from 'axios';

const TaskList = () => {
  const [tasks, setTasks] = useState([]);
  const [isModalOpen, setIsModalOpen] = useState(false);

  // Fetch tasks from backend
  useEffect(() => {
    const fetchTasks = async () => {
      try {
        const userId = localStorage.getItem('id');
        const response = await axios.get('/api/get-all-tasks', {
          headers: { id: userId }
        });
        setTasks(response.data.data);
      } catch (error) {
        console.error('Error fetching tasks:', error);
      }
    };

    fetchTasks();
  }, []);

  // Add a new task
  const handleAddTask = async (newTask) => {
    try {
      const userId = localStorage.getItem('id');
      const response = await axios.post('/api/create-task', newTask, {
        headers: { id: userId }
      });
      setTasks([...tasks, response.data.task]);
    } catch (error) {
      console.error('Error adding task:', error);
    }
  };

  // Delete a task
  const handleDeleteTask = async (taskId) => {
    try {
      const userId = localStorage.getItem('id');
      await axios.delete(`/api/delete-task/${taskId}`, {
        headers: { id: userId }
      });
      setTasks(tasks.filter(task => task._id !== taskId));
    } catch (error) {
      console.error('Error deleting task:', error);
    }
  };

  // Update task importance
  const handleToggleImportant = async (taskId) => {
    try {
      await axios.put(`/api/update-imp-task/${taskId}`);
      setTasks(tasks.map(task =>
        task._id === taskId ? { ...task, important: !task.important } : task
      ));
    } catch (error) {
      console.error('Error toggling task importance:', error);
    }
  };

  // Update task completion status
  const handleToggleCompletion = async (taskId) => {
    try {
      await axios.put(`/api/update-complete-task/${taskId}`);
      setTasks(tasks.map(task =>
        task._id === taskId ? { ...task, complete: !task.complete } : task
      ));
    } catch (error) {
      console.error('Error toggling task completion:', error);
    }
  };

  return (
    <div className="task-list">
      {tasks.filter(task => !task.trashed).map(task => (
        <Task
          key={task._id}
          task={task}
          onDelete={handleDeleteTask}
          onToggleImportant={handleToggleImportant}
          onToggleCompletion={handleToggleCompletion}
        />
      ))}
      <button id="add" className="task-list" onClick={() => setIsModalOpen(true)}>
        <i id="more" className="bi bi-plus-circle"></i>
        <p id="p">Add Task</p>
      </button>
      <InputData isOpen={isModalOpen} onClose={() => setIsModalOpen(false)} onSubmit={handleAddTask} />
    </div>
  );
};

export default TaskList;
