import React, {useState} from 'react';
import './task.css';

const Task = ({ task, onMoveToTrash }) => {
  const [isToggled, setIsToggled] = useState(false);

  const handleToggle = () => {
    setIsToggled((prev) => !prev);
  }

  const [checked, setIsChecked] = useState(false)

  const handleCheck =() => {
    setIsChecked((prev) => !prev);

  }
  return (
    <div className="task">
      <div className="task-content">
        <h3>{task.title}</h3>
        <p>{task.description}</p>
      </div>
      <div className="task-buttons">

        <button onClick={handleCheck}>
      {checked ? (
       <h6 id='co'>Completed </h6>

      ) : (
        <h6 id='inco'>incompleted</h6>
      )}</button>


        <button><i id='icon' className="bi bi-pencil"></i></button>

        <button onClick={() => onMoveToTrash(task.id)}><i id="icon" className="bi bi-trash"></i></button>


        <button onClick={handleToggle}>
      {isToggled ? (
       <i className="bi bi-star-fill" style={{ color: 'yellow' }}></i>

      ) : (
        <i id='icon' className="bi bi-star"></i>
      )}
    </button>  
        </div>
    </div>
  );
};

export default Task;










































