import React, { useState } from "react";
import './signUp.css';
import { Link } from 'react-router-dom'; // Import Link from react-router-dom

function LoginSignup() {
  const [action, setAction] = useState("Sign Up");

  return (
    <div className="container">
      <div className="header">
        <div className="text">{action}</div>
        <div className="underline"></div>
      </div>
      <div className="inputs">
        {/* Include the Name input only for Sign Up */}
        <div className="input">
          <img src="./img/icons8-person-24.png" alt="person icon" />
          <input type="text" placeholder="Name" required />
        </div>

        <div className="input">
          <img src="./img/icons8-email-30.png" alt="email icon" />
          <input type="email" placeholder="Email" required />
        </div>

        <div className="input">
          <img id="password" src="./img/icons8-password-50.png" alt="password icon" />
          <input type="password" placeholder="Password" required />
        </div>
      </div>

      <div className="submit-container">
        <div className="submit" onClick={() => setAction("Sign Up")}>Sign Up</div>
      </div>

      {/* Add link to login page */}
      <div className="forgot-passwordd">
        Already having an account? <Link to="/signIn" className="login-link">Login here</Link>
      </div>
    </div>
  );
}

export default LoginSignup;
