import React, { useState, useEffect } from 'react';
import Header from "./header";
import Sidebar from "./sidebar";
import TaskList from "./taskList";
import Task from './task';
import InputData from './inputData';
import './tasklist.css';
import axios from 'axios';

function Home(){
  const [tasks, setTasks] = useState([]);
  const [Data, setData] = useState();
  const [isModalOpen, setIsModalOpen] = useState(false);
  const headers = {
    id: localStorage.getItem("id"),
    authorization: `Bearer ${localStorage.getItem("token")}`
  };

  // Fetch tasks from backend
  useEffect(() => {
    const fetch = async () => {
      try {
        // const userId = localStorage.getItem('id'); // Assuming the user ID is stored in localStorage
        // const token = localStorage.getItem('token'); // Assuming token is stored in localStorage
        const response = await axios.get('http://localhost:4000/api/v2/get-all-tasks', {
          // headers: { id: userId, Authorization: `Bearer ${token}` }
        });
        setData(response.data.data);
      } catch (error) {
        console.error('Error fetching tasks:', error);
      }
    };

    fetch();
  }, []);
Data && console.log(Data.tasks);
  // Add a new task by sending it to the backend
  const handleAddTask = async (newTask) => {
    try {
      setTasks([...tasks, newTask]); // Add the new task to the state
    } catch (error) {
      console.error('Error adding task:', error);
    }
  };

  // Delete a task by sending a delete request to the backend
  const moveToTrash = async (taskId) => {
    try {
      const userId = localStorage.getItem('id');
      const token = localStorage.getItem('token');
      await axios.delete(`/api/v2/delete-task/${taskId}`, {
        headers: { id: userId, Authorization: `Bearer ${token}` }
      });
      setTasks(tasks.filter(task => task._id !== taskId)); // Remove the task from state
    } catch (error) {
      console.error('Error deleting task:', error);
    }
  };

  // Update the task as important by sending a put request to the backend
  const toggleImportant = async (taskId) => {
    try {
      const response = await axios.put(`/api/v2/update-imp-task/${taskId}`, {}, {
        headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
      });
      setTasks(tasks.map(task =>
        task._id === taskId ? { ...task, important: !task.important } : task
      ));
    } catch (error) {
      console.error('Error toggling task importance:', error);
    }
  };

  // Update task completion status
  const toggleCompletion = async (taskId) => {
    try {
      const response = await axios.put(`/api/v2/update-complete-task/${taskId}`, {}, {
        headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
      });
      setTasks(tasks.map(task =>
        task._id === taskId ? { ...task, completed: !task.completed } : task
      ));
    } catch (error) {
      console.error('Error toggling task completion:', error);
    }
  };

  const filteredTasks = tasks.filter(task => !task.trashed);
  return(
    <>
     <Header />
     <div className="app-body">
            <Sidebar />
            <TaskList />
          </div>



    <div className="task-list">
      {filteredTasks.map(task => (
        <Task 
          key={task._id} 
          task={task} 
          onMoveToTrash={moveToTrash} 
          onToggleImportant={toggleImportant}
          onToggleCompletion={toggleCompletion}
        />
      ))}
      <button id="add" className="task-list" onClick={() => setIsModalOpen(true)}>
        <i id="more" className="bi bi-plus-circle"></i>
        <p id="p">Add Task</p>
      </button>
      <InputData isOpen={isModalOpen} onClose={() => setIsModalOpen(false)} onSubmit={handleAddTask} />
    </div>



    </>
  )
}
export default Home;
