    setTasks(tasks.map(task => 
      task.id === id ? { ...task, isImportant: !task.isImportant } : task
    ));
  };

  return (
    <div className="app">
      <Header />
      <div className="app-body">
        <Sidebar />
        <TaskList tasks={tasks} toggleImportance={toggleImportance} />
        <ImportantTasksPage tasks={tasks} toggleImportance={toggleImportance} />
        </div>

