import React, { useState, useEffect } from "react";
import Header from "./Header";
import Sidebar from "./Sidebar";
import TaskList from "./TaskList";
import axios from "axios";

function Home() {
  const [tasks, setTasks] = useState([]);
  const headers = {
    'id': localStorage.getItem("id"),
    'authorization': `Bearer ${localStorage.getItem("token")}`,
  };

  useEffect(() => {
    const fetch = async () => {
      try {
        const response = await axios.get("http://localhost:4000/api/v2/get-all-tasks", { headers });
        setTasks(response.data.data); // Ensure correct path to data
      } catch (error) {
        console.error("Error fetching data:", error);
        // Optionally set error state and display a message
      }
    };
    fetch();
  }, []);

  const handleAddTask = (newTask) => {
    setTasks([...tasks, { ...newTask, id: tasks.length + 1, status: 'incompleted' }]);
  };

  const handleMoveToTrash = (taskId) => {
    setTasks(tasks.map(task =>
      task.id === taskId ? { ...task, trashed: true } : task
    ));
  };

  return (
    <>
      <Header />
      <div className="app-body">
        <Sidebar />
        <TaskList tasks={tasks} onAddTask={handleAddTask} onMoveToTrash={handleMoveToTrash} />
      </div>
    </>
  );
}

export default Home;
