import React, { useState } from "react";
import './signUp.css';
import { Link, useNavigate } from 'react-router-dom'; // Import Link from react-router-dom
import axios from "axios";

function Signup() {
  const [action, setAction] = useState("Sign Up");
  const [Data, setData] = useState({ username: "", email: "", password: "" });
  const history = useNavigate();

  // Handler for input changes
  const change = (e) => {
    const { name, value } = e.target;
    setData({ ...Data, [name]: value });
  };

  // Submit handler
 // Submit handler
const submit = async () => {
  try {
    if (Data.username === "" || Data.email === "" || Data.password === "") {
      alert("All fields are required");
    } else {
      const response = await axios.post("http://localhost:4000/api/v1/sign-up", Data);
      // Handle success (e.g., redirect to login page or show success message)
      alert("User registered successfully");
      navigate("/signIn"); // Use navigate to redirect to the login page
    }
  } catch (error) {
    // Check if error.response exists before accessing its data
    const errorMessage = error.response?.data?.message || "An error occurred during registration.";
    alert(errorMessage);
  }
};

  return (
    <div className="container">
      <div className="header">
        <div className="text">{action}</div>
        <div className="underline"></div>
      </div>
      <div className="inputs">
        <div className="input">
          <img src="./img/icons8-person-24.png" alt="person icon" />
          <input 
            type="text" 
            name="username" // Added name attribute
            placeholder="Name" 
            required 
            value={Data.username}
            onChange={change} 
          />
        </div>

        <div className="input">
          <img src="./img/icons8-email-30.png" alt="email icon" />
          <input 
            type="email" 
            name="email" // Added name attribute
            placeholder="Email" 
            required 
            value={Data.email}
            onChange={change} 
          />
        </div>

        <div className="input">
          <img id="password" src="./img/icons8-password-50.png" alt="password icon" />
          <input 
            type="password" 
            name="password" // Added name attribute
            placeholder="Password" 
            required 
            value={Data.password}
            onChange={change} 
          />
        </div>
      </div>

      <div className="submit-container">
        <button onClick={submit} className="submit">Sign Up</button>
      </div>

      {/* Add link to login page */}
      <div className="forgot-passwordd">
        Already having an account? <Link to="/signIn" className="login-link">Login here</Link>
      </div>
    </div>
  );
}

export default Signup;
