import React, { useState } from "react";
import './signUp.css';
import { Link, useNavigate } from 'react-router-dom'; // Import Link from react-router-dom
import axios from "axios";

function SignIn() {
  // Set default action to "Login" for the sign-in page
  const [action, setAction] = useState("Login");
const [Data, setData] = useState({username: "", password: ""});
const change = (e) => {
  const { name, value } = e.target;
  setData({ ...Data, [name]: value });
};

// Submit handler
const submit = async () => {
  try{
  if (Data.username === "" || Data.email === "" || Data.password === "") {
    alert("All fields are required");
  } else {
      const response = await axios.post("http://localhost:4000/api/v1/log-in", Data);
      // Handle success (e.g., redirect to login page or show success message)
      setData({username: "",  password:""})
      //alert("User registered successfully");
      localStorage.setItem("id", response.data)
    } }
   catch (error) {
      // Handle error (e.g., show error message)
      alert("An error occurred during registration");
    }
};
  return (
    <div className="container">
      <div className="header">
        <div className="text">{action}</div>
        <div className="underline"></div>
      </div>
      <div className="input">
          <img src="./img/icons8-person-24.png" alt="person icon"/>
        <input type="text" placeholder="Username" required 
        value={Data.username}
        onChange={change}
        />
      </div>

      <div className="input">
        <img id="password" src="./img/icons8-password-50.png" alt="password icon" />
        <input type="password" placeholder="Password" required 
        value={Data.username}
        onChange={change}
        />
      </div>

      {/* Lost password option is only shown on the login page */}
      <div className="submit-container">
        {/* Only show the Login button as active */}
        <button className="submit" onClick={submit}>Login</button>
      </div>
      <div className="forgot-password">
        Lost Password? <span>Click here</span>
      </div>

      <div className="forgot-password">
        Not having an account? <Link to="/signUp" className="signup-link">Sign Up here</Link>
      </div>
    </div>
  );
}

export default SignIn;
