import React, {useState} from 'react';
import './task.css';

const Task = ({ task, toggleImportance }) => {
  const [isToggled, setIsToggled] = useState(false);

  const handleToggle = () => {
    setIsToggled((prev) => !prev);
  }
  return (
    <div className="task">
      <div className="task-content">
        <h3>{task.title}</h3>
        <p>{task.description}</p>
      </div>
      <div className="task-buttons">
        <button id='uff' className={task.status === 'completed' ? 'completed' : 'incompleted'}>
          {task.status === 'completed' ? 'Completed' : ' inCompleted'}
        </button>
        <button><i id='icon' className="bi bi-pencil"></i></button>
        <button><i id='icon' className="bi bi-trash"></i></button>

        <button onClick={() => toggleImportance(task.id)}>
          {task.isImportant ? (
            <i className="bi bi-star-fill" style={{ color: 'yellow' }}></i>
          ) : (
            <i id='icon' className="bi bi-star"></i>
          )}
        </button>

        </div>
    </div>
  );
};

export default Task;
