import React, {use} from 'react';
import Header from './components/header';
import Sidebar from './components/sidebar';
import TaskList from './components/taskList';
import './App.css';

  const App = () => {
    const [tasks, setTasks] = useState([
      { id: 1, title: 'test 1', description: ' laboris nisi ut aliquip ex ea commodo consequat...', status: 'incompleted', isImportant: false },
      { id: 2, title: 'Build penny juice app', description: 'Lorem ipsum dolor sit amet...', status: 'completed', isImportant: false },
      { id: 3, title: 'Learn Python', description: 'consectetur adipiscing elit...', status: 'incompleted', isImportant: false },
      { id: 4, title: 'Learn React', description: 'consectetur adipiscing elit...', status: 'incompleted', isImportant: false },
      { id: 5, title: 'Build a Portfolio', description: 'consectetur adipiscing elit...', status: 'completed', isImportant: false },
      { id: 6, title: 'Learn Node.js', description: 'consectetur adipiscing elit...', status: 'incompleted', isImportant: false },
    ]);

    const toggleImportance = (id) => {
      setTasks(tasks.map(task => 
        task.id === id ? { ...task, isImportant: !task.isImportant } : task
      ));
    };
  return (
    <div className="app">
      <Header />
      <div className="app-body">
        <Sidebar />
        <TaskList tasks={tasks} toggleImportance={toggleImportance} />
        <ImportantTasksPage tasks={tasks} toggleImportance={toggleImportance} />
        </div>

    </div>
  );
}

export default App;
