import React, { useState } from 'react';
import Task from './task';
import './tasklist.css';


const TaskList = () => {
  const [tasks, setTasks] = useState([
    { id: 1, title: 'test 1', description: ' laboris nisi ut aliquip ex ea commodo consequat. Duis aute irur laboris nisi ut aliquip ex ea commodo consequat. Duis aute irure dolor in reprehenderit in voluptate ', status: 'incompleted' },
    { id: 2, title: 'Build penny juice app', description: 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut enim ad ', status: 'completed' },
    { id: 3, title: 'Learn Python', description: 'consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut enim ad minim ', status: 'incompleted' },
    { id: 4, title: 'Learn React', description: 'consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut enim ad minim v', status: 'incompleted' },
    { id: 5, title: 'Build a Portfolio', description: 'consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut enim a ullamco', status: 'completed' },
      { id: 3, title: 'Learn Nodejs', description: 'consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut enim ad ', status: 'incompleted' },


  ]);
  const toggleImportance = (id) => {
    setTasks(tasks.map(task => 
      task.id === id ? { ...task, isImportant: !task.isImportant } : task
    ));
  };
  return (
    <div className="task-list">
    {tasks.map(task => (
      <Task key={task.id} task={task} toggleImportance={toggleImportance} />
    ))}
    <button id='add' className="task-list">
      <i id='more' className="bi bi-plus-circle"></i>
      <p id='p'>Add Task</p>
    </button>
  </div>
  );
};

export default TaskList;
