import React, { useState } from 'react';
import './task.css';

const Task = ({ task, onMoveToTrash }) => {
  const [isToggled, setIsToggled] = useState(task.important);
  const [checked, setIsChecked] = useState(task.complete);

  const toggleImportant = async () => {
    try {
      const token = localStorage.getItem('token');
      await fetch(`http://localhost:4000/api/v2/update-imp-task/${task._id}`, {
        method: 'PUT',
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json',
        },
      });
      setIsToggled(!isToggled);
    } catch (error) {
      console.error('Error updating task importance:', error);
    }
  };

  const toggleCompletion = async () => {
    try {
      const token = localStorage.getItem('token');
      await fetch(`http://localhost:4000/api/v2/update-complete-task/${task._id}`, {
        method: 'PUT',
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json',
        },
      });
      setIsChecked(!checked);
    } catch (error) {
      console.error('Error updating task completion:', error);
    }
  };

  return (
    <div className="task">
      <div className="task-content">
        <h3>{task.title}</h3>
        <p>{task.description}</p>
      </div>
      <div className="task-buttons">
        <button onClick={toggleCompletion}>
          {checked ? <h6 id='co'>Completed</h6> : <h6 id='inco'>Incomplete</h6>}
        </button>
        <button onClick={() => onMoveToTrash(task.id)}><i id="icon" className="bi bi-trash"></i></button>
        <button onClick={toggleImportant}>
          {isToggled ? (
            <i className="bi bi-star-fill" style={{ color: 'yellow' }}></i>
          ) : (
            <i id='icon' className="bi bi-star"></i>
          )}
        </button>
      </div>
    </div>
  );
};

export default Task;
