import React from 'react';
import './task.css';

const Task = ({ task }) => {
  return (
    <div className="task">
      <div className="task-content">
        <h3>{task.title}</h3>
        <p>{task.description}</p>
      </div>
      <div className="task-buttons">
        <button id='uff' className={task.status === 'completed' ? 'completed' : 'incompleted'}>
          {task.status === 'completed' ? 'Completed' : ' nCompleted'}
        </button>
        <button><i className="bi bi-pencil"></i></button>
        <button><i className="bi bi-trash"></i></button>
      </div>
    </div>
  );
};

export default Task;
