import React from 'react';
import './header.css';
import { useDispatch } from 'react-redux';
import { authActions } from '../store/auth';
import { useNavigate } from 'react-router-dom';
const Header = () => {
  const dispatch = useDispatch();
  const history = useNavigate()
  const logout = () =>{
    dispatch(authActions.logout());
    localStorage.clear("id");
    localStorage.clear("token");

  }
  return (
    <header className="header">
     {/*  <h1>Taskify</h1>
      <div className="user-info">
      <button><i id='icon' className="bi bi-person-circle"></i></button>
        <button>Log Out</button>
      </div> */}
      <nav class="navbar  fixed-top">
  <div class="container-fluid">
    <a class="navbar-brand" href="#"><h1>Taskify</h1></a>
    {/*<form class="d-flex mt-3" role="search">
          <input class="form-control me-2" type="search" placeholder="Search" aria-label="Search"/>
          <button class="btn btn-outline-success" type="submit">Search</button>
        </form>*/}
   <button
  class="navbar-toggler custom-toggler"
  type="button"
  data-bs-toggle="offcanvas"
  data-bs-target="#offcanvasNavbar"
  aria-controls="offcanvasNavbar"
  aria-label="Toggle navigation"
>
<span class="custom-toggler-icon"></span>
</button>


    <div class="offcanvas offcanvas-end" tabindex="-1" id="offcanvasNavbar" aria-labelledby="offcanvasNavbarLabel">
      <div class="offcanvas-header">
        <h1 class="offcanvas-title" id="offcanvasNavbarLabel">Taskify</h1>

          <button type="button" class="btn-close" data-bs-dismiss="offcanvas" aria-label="Close"></button>
      </div>
      <div class="offcanvas-body">
        <ul class="navbar-nav justify-content-end flex-grow-1 pe-3">
          <li class="nav-item">
            <a class="nav-link" aria-current="page" href="#"><i id='iconn' className="bi bi-person-circle"></i> <h5>Account</h5></a>
          </li>
          <li class="nav-item">
            <a class="nav-link" href="#"><i id='iconn' className="bi bi-gear"></i> <h5> Setting</h5>
            </a>
          </li>
          <li class="nav-item">
            <a class="nav-link" href="/signUp"><i id='iconn' className="bi bi-box-arrow-right" onClick={logout}></i> <h5> Logout</h5>
            </a>
          </li>
        </ul>
      </div>
    </div>
  </div>
</nav>
    </header>
  );
};

export default Header;
