import React, { useEffect, useState } from 'react';
import Task from './task';
import Header from "./header";
import Sidebar from "./sidebar";

const Important = () => {
  const [importantTasks, setImportantTasks] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    // Function to fetch important tasks
    const fetchImportantTasks = async () => {
      try {
        const token = localStorage.getItem('token');

        const response = await fetch('http://localhost:4000/api/v2/tasks/important', {
          method: 'GET',
          headers: {
            'Authorization': `Bearer ${token}`,
          },
        });

        if (response.ok) {
          const data = await response.json();
          setImportantTasks(data);
        } else {
          console.error('Failed to fetch important tasks');
        }
      } catch (error) {
        console.error('Error fetching important tasks:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchImportantTasks();
  }, []);

  // Function to handle task updates (e.g., marking as complete or important)
  const handleUpdate = () => {
    // Re-fetch tasks or update state after a task modification
    fetchImportantTasks();
  };

  // Function to handle task deletion
  const handleDelete = async (taskId) => {
    try {
      const token = localStorage.getItem('token');

      const response = await fetch(`http://localhost:4000/api/v2/delete-task/${taskId}`, {
        method: 'DELETE',
        headers: {
          'Authorization': `Bearer ${token}`,
        },
      });

      if (response.ok) {
        // Remove the deleted task from state
        setImportantTasks(importantTasks.filter(task => task._id !== taskId));
      } else {
        console.error('Failed to delete task');
      }
    } catch (error) {
      console.error('Error deleting task:', error);
    }
  };

  if (loading) {
    return <div>Loading...</div>;
  }

  return (
    <div>
      <Header />
      <div className="app-body">
        <Sidebar />
        <div className="tasks-container">
          {importantTasks.length > 0 ? (
            importantTasks.map((task) => (
              <Task 
                key={task._id} 
                task={task} 
                onDelete={handleDelete} 
                onUpdate={handleUpdate} 
              />
            ))
          ) : (
            <p>No important tasks found.</p>
          )}
        </div>
      </div>
    </div>
  );
};

export default Important;
