import React, { useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, useNavigate } from 'react-router-dom';
import { useDispatch, useSelector } from "react-redux";
import { authActions } from './store/auth';

import Home from './components/home';
import './App.css';
import Important from './components/important';
import Trash from './components/Trash';
import Incompleted from './components/incompleted';
import Completed from './components/completed';
import SignUp from './components/signUp';
import SignIn from './components/signIn';

function App() {
  const navigate = useNavigate(); // Move useNavigate to the top
  const isLoggedIn = useSelector((state) => state.auth.isLoggedIn); // Move useSelector to the top
  const dispatch = useDispatch();

  useEffect(() => {
    // Check if user is logged in based on local storage
    if (localStorage.getItem("id") && localStorage.getItem("token")) {
      dispatch(authActions.login());
    } else if (!isLoggedIn) {
      navigate("/signUp"); // Navigate to sign-up page if not logged in
    }
  }, [isLoggedIn, navigate, dispatch]); // Include dependencies in the useEffect array

  // Do not place navigation logic outside useEffect
  if (isLoggedIn) {
    navigate("/");
  }

  return (
    <Routes>
      <Route path="/" element={<Home />} />
      <Route path="/important" element={<Important />} />
      <Route path="/completed" element={<Completed />} />
      <Route path="/incompleted" element={<Incompleted />} />
      <Route path="/Trash" element={<Trash />} />
      <Route path="/signUp" element={<SignUp />} />
      <Route path="/signIn" element={<SignIn />} />
    </Routes>
  );
}

export default App;
