import React, { useState } from 'react';
import Task from './Task'; // Ensure the path is correct
import InputData from './InputData'; // Ensure the path is correct
import './tasklist.css';

const TaskList = ({ tasks, onAddTask, onMoveToTrash }) => { // Destructure tasks and functions from props
  const [isModalOpen, setIsModalOpen] = useState(false);

  const handleAddTask = (newTask) => {
    onAddTask(newTask); // Call the parent function to add a task
    setIsModalOpen(false); // Close the modal after adding
  };

  const filteredTasks = tasks.filter(task => !task.trashed);

  return (
    <div className="task-list">
      {filteredTasks.map(task => (
        <Task key={task.id} task={task} onMoveToTrash={onMoveToTrash} />
      ))}
      <button id="add" className="task-list" onClick={() => setIsModalOpen(true)}>
        <i id="more" className="bi bi-plus-circle"></i>
        <p id="p">Add Task</p>
      </button>
      <InputData isOpen={isModalOpen} onClose={() => setIsModalOpen(false)} onSubmit={handleAddTask} />
    </div>
  );
};

export default TaskList;
