import React, { useState } from 'react';
import Task from './task';
import './tasklist.css';


const TaskList = () => {
  const [tasks, setTasks] = useState([
    { id: 1, title: 'test 1', description: ' laboris nisi ut aliquip ex ea commodo consequat. Duis aute irure dolor in reprehenderit in voluptate velit esse cillum dolore eu fugiat nulla pariatur', status: 'incompleted' },
    { id: 2, title: 'The Best Coding Channel', description: 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut enim ad minim veniam, quis nostrud exercitation ullamco', status: 'completed' },
    { id: 3, title: 'CPP concepts', description: 'I need to clear basics of Cpp...', status: 'incompleted' },
    { id: 4, title: 'Learn React', description: 'Finish the React course', status: 'incompleted' },
    { id: 5, title: 'Build a Portfolio', description: 'Create my portfolio website', status: 'completed' },
      { id: 3, title: 'CPP concepts', description: 'I need to clear basics of Cpp...', status: 'incompleted' },


  ]);

  return (
    <div className="task-list">
      {tasks.map(task => (
        <Task key={task.id} task={task} />
      ))}
      <button id='add' className="task-list">
      <i id='more' className="bi bi-plus-circle"></i>
      <p id='p' >Add Task</p>
      </button>
    </div>
  );
};

export default TaskList;
