import React, { useState } from 'react';
import './task.css';

const Task = ({ task, onDelete,onClose }) => {
  const [isEditing, setIsEditing] = useState(false);
  const [title, setTitle] = useState(task.title);
  const [description, setDescription] = useState(task.desc);
  const [checked, setChecked] = useState(task.complete);

  const handleEditToggle = () => {
    setIsEditing(!isEditing);
  };

  const handleEditTask = async () => {
    try {
      const token = localStorage.getItem('token');

      const response = await fetch(`http://localhost:4000/api/v2/update-task/${task._id}`, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`,
        },
        body: JSON.stringify({ title, desc: description }),
      });

      if (response.ok) {
        setIsEditing(false);
      } else {
        console.error('Failed to update the task');
      }
    } catch (error) {
      console.error('Error updating task:', error);
    }
  };

  const handleCheck = async () => {
    try {
      const token = localStorage.getItem('token');

      const response = await fetch(`http://localhost:4000/api/v2/update-complete-task/${task._id}`, {
        method: 'PUT',
        headers: {
          'Authorization': `Bearer ${token}`,
        },
      });

      if (response.ok) {
        setChecked(!checked);
      } else {
        console.error('Failed to update task completion');
      }
    } catch (error) {
      console.error('Error updating task completion:', error);
    }
  };

  return (
    <div className="task">
      <div className="task-content">
        {isEditing ? (
          <>
            {/* <input
              type="text"
              value={title}
              onChange={(e) => setTitle(e.target.value)}
              className="task-edit-input"
            />
            <textarea
              value={description}
              onChange={(e) => setDescription(e.target.value)}
              className="task-edit-textarea"
            ></textarea>
            <button onClick={handleEditTask} className="task-save-button">
              Save
            </button>
            <button onClick={handleEditToggle} className="task-cancel-button">
              Cancel
            </button> */}
            <div className="modal-overlay">
      <div className="modal-content">
        <button className="close-button" onClick={onClose}>
          &times;
        </button>
        <h2 id='newTask'>Edit Task</h2>
        <input
          type="text"
          placeholder="Title"
          value={title}
          onChange={(e) => setTitle(e.target.value)}
          className="modal-input"
        />
        <textarea
          placeholder="Description"
          value={description}
          onChange={(e) => setDescription(e.target.value)}
          className="modal-textarea"
        ></textarea>
        <button onClick={handleEditTask} className="task-save-button">
              Save
            </button><br/>
            <button onClick={handleEditToggle} className="task-cancel-button">
              Cancel
            </button>
      </div>
    </div>
          </>
        ) : (
          <>
            <h3>{task.title}</h3>
            <hr/>
            <p>{task.desc}</p>
          </>
        )}
      </div>
      <div className="task-buttons">
        <button onClick={handleCheck}>
          {checked ? (
            <h6 id='co'>Completed</h6>
          ) : (
            <h6 id='inco'>Incomplete</h6>
          )}
        </button>

        <button onClick={handleEditToggle}>
          <i id='icon' className="bi bi-pencil"></i>
        </button>

        <button onClick={() => onDelete(task._id)}>
          <i id="icon" className="bi bi-trash"></i>
        </button>

        <button onClick={async () => {
          try {
            const token = localStorage.getItem('token');

            const response = await fetch(`http://localhost:4000/api/v2/update-imp-task/${task._id}`, {
              method: 'PUT',
              headers: {
                'Authorization': `Bearer ${token}`,
              },
            });

            if (response.ok) {
            } else {
              console.error('Failed to update task importance');
            }
          } catch (error) {
            console.error('Error updating task importance:', error);
          }
        }}>
          {task.important ? (
            <i className="bi bi-star-fill" style={{ color: 'yellow' }}></i>
          ) : (
            <i id='icon' className="bi bi-star"></i>
          )}
        </button>
      </div>
    </div>
  );
};

export default Task;
