import React from 'react';
import './task.css';

const Task = ({ task }) => {
  return (
    <div className="task">
      <div className="task-content">
        <h3>{task.title}</h3>
        <p>{task.description}</p>
      </div>
      <div className="task-buttons">
        <button className={task.status === 'completed' ? 'completed' : 'incompleted'}>
          {task.status === 'completed' ? 'Completed' : 'In Completed'}
        </button>
        <button><img src='public/img/icons8-delete-64.png'/></button>
        <button>Delete</button>
      </div>
    </div>
  );
};

export default Task;
