import React, { useState } from "react";
import './signUp.css';
import { Link } from 'react-router-dom'; // Import Link from react-router-dom
import axios from "axios";

function SignIn() {
  // Set default action to "Login" for the sign-in page
  const [action, setAction] = useState("Login");
const [Data, setData] = useState({username: "", email:"", password: ""})
  return (
    <div className="container">
      <div className="header">
        <div className="text">{action}</div>
        <div className="underline"></div>
      </div>
      <div className="input">
        <img src="./img/icons8-email-30.png" alt="email icon" />
        <input type="email" placeholder="Email" required />
      </div>

      <div className="input">
        <img id="password" src="./img/icons8-password-50.png" alt="password icon" />
        <input type="password" placeholder="Password" required />
      </div>

      {/* Lost password option is only shown on the login page */}
      <div className="submit-container">
        {/* Only show the Login button as active */}
        <div className="submit" onClick={() => setAction("Login")}>Login</div>
      </div>
      <div className="forgot-password">
        Lost Password? <span>Click here</span>
      </div>
      {/* Add link to sign-up page */}
      <div className="forgot-password">
        Not having an account? <Link to="/signUp" className="signup-link">Sign Up here</Link>
      </div>
    </div>
  );
}

export default SignIn;
